import {CodelabConfig} from "../app/codelab-config";
import {FileConfig} from "../app/file-config";
import {AppComponent} from "./files/AppComponent";
import {AppModule} from "./files/AppModule";
import {Bootstrap} from "./files/Bootstrap";
